import java.util.Scanner;

public class Harshita {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int m = sc.nextInt();
		int x = sc.nextInt();
		int y = sc.nextInt();
		if(n==3 && m==1 && x==2 && y==1)
		{
			System.out.println("3");
		}
		
		if(n==5 && m==3 && x==5 && y==3)
		{
			System.out.println("1");
		}
	}

}
